var MODULE = "security";


/*******************************************************************************************
 * Required Module(s)
 ******************************************************************************************/

/*******************************************************************************************
 * Global Variables
 ******************************************************************************************/

/*******************************************************************************************
 * User List For Basic Authentication
 ******************************************************************************************/

var userList= [	
        {username:'tempuser',password:'temp098'}
		      ];



/*******************************************************************************************
 * To Check whether the user is Authorized
 ******************************************************************************************/

var authorize = function (username, password) {

	var isAuthorized = false;
	
	try {
		
		for(var i = 0; i < userList.length; i++) {
			 var userObj=userList[i];
			 
			 if(userObj.username == username && userObj.password == password) {
					isAuthorized = true;
					return isAuthorized;
			 }
		 }

	} 
	catch (err) {
		console.log('Error @ [' + MODULE + '] Authorizing a User : ' + err);
	}
    return isAuthorized;
};

exports.authorize = authorize;